    public static ItemDataBase[] GetList(ItemType itemType, string search)
    {
      List<ItemDataBase> list = ItemDataList.GetList(itemType);
      if (list == null)
        return (ItemDataBase[]) null;
      if (search == null)
        return list.ToArray();
      Regex regex = new Regex(search, RegexOptions.IgnoreCase);
      List<ItemDataBase> itemDataBaseList = new List<ItemDataBase>();
      foreach (ItemDataBase itemDataBase in list)
      {
        if (regex.IsMatch(itemDataBase.Name))
          itemDataBaseList.Add(itemDataBase);
      }
      return itemDataBaseList.ToArray();
    }

