    public static ItemDataBase GetItem(ItemType itemType, ushort itemValue)
    {
      List<ItemDataBase> list = ItemDataList.GetList(itemType);
      if (list != null)
      {
        foreach (ItemDataBase itemDataBase in list)
        {
          if ((int) itemDataBase.Value == (int) itemValue)
            return itemDataBase;
        }
      }
      return (ItemDataBase) null;
    }

    public static ItemDataBase GetItem(ushort itemValue)
    {
      for (int index = 0; index < 10; ++index)
      {
        List<ItemDataBase> list = ItemDataList.GetList((ItemType) index);
        if (list != null)
        {
          foreach (ItemDataBase itemDataBase in list)
          {
            if ((int) itemDataBase.Value == (int) itemValue)
              return itemDataBase;
          }
        }
      }
      return (ItemDataBase) null;
    }

    public static ItemDataBase GetSmartItemFromIndex(int dataIndex)
    {
      dataIndex -= 7;
      int num = 0;
      for (ItemType itemType = ItemType.Weapon; itemType < ItemType.MAX; ++itemType)
      {
        List<ItemDataBase> list = ItemDataList.GetList(itemType);
        num += list.Count;
        if (num > dataIndex)
        {
          foreach (ItemDataBase smartItemFromIndex in list)
          {
            if (smartItemFromIndex.DataIndex == dataIndex)
              return smartItemFromIndex;
          }
        }
      }
      return (ItemDataBase) null;
    }
